# ## Importing Modules

# In[1]:


from math import cos, pi, exp
import pyqtgraph as pg
import numpy as np
from pyqtgraph import PlotWidget,mkPen
from PyQt5 import QtCore, QtGui, QtWidgets
import sys,math



# ## Task 1a

# In[2]:


X1 = np.arange(0,500,3)
#y1 = np.exp(x)
#y2 = np.log(x)
y1 = np.zeros(len(X1))
y2 = np.zeros(len(X1))
for i,x in enumerate(X1):
    x_rad = x*pi / 100
    y1[i] = 300 - 100*cos(2*x_rad) + 30*cos(4*x_rad) + 6*cos(6*x_rad)
    y2[i] = 1/y1[i]


# In[3]:


# Create layout to hold multiple subplots
pg_layout = pg.GraphicsLayoutWidget()
#configure
pg_layout.setWindowTitle('B19102104 Muhammad Umar Anzar')
pen = pg.mkPen(color=(114,137,218))
pg_layout.setBackground('#282b30') 
#plot
pg_layout.addPlot(x=X1, y=y1, row=0, col=0, pen=pen, title="function 300-100*cos(2*x*pi/100)+30*cos(4*x*pi/100)+6*cos(6*x*pi/100)", name="red")
pg_layout.addPlot(x=X1, y=y2, row=1, col=0, pen=pen, title="inverse of funtion")
pg_layout.show()


